import "./reviewCard.css";
import { useState, useRef, useEffect } from "react";
import axios from 'axios';

const delay = 2500;

export default function ReviewCard( {user} ) {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const [index, setIndex] = useState(0);
    const [reviews, setReviews] = useState([]); 
    const timeoutRef = useRef(null);

    const resetTimeout = () => {
        if (timeoutRef.current) {
            clearTimeout(timeoutRef.current);
        }
    };

    const fetchReview = async (reviewId) => {
            return (await axios.get(`/reviews/get?reviewId=${reviewId}`)).data};

    useEffect(() => {
        const fetchReviews = async () => {
            const res = await user.reviews.map((id)=>fetchReview(id));
            setReviews(res);
        };
        fetchReviews();
    },[user]);

    // useEffect(() => {
    //     resetTimeout()
    //     timeoutRef.current = setTimeout(
    //         () =>
    //             setIndex((prevIndex) =>
    //                 prevIndex === reviews.length - 1 ? 0 : prevIndex + 1
    //             ),
    //         delay
    //     );

    //     return () => {
    //         resetTimeout();
    //     };
    // }, [index]);

    return (
        <div className="slideshow">
            <div
                className="slideshowSlider"
                style={{ transform: `translate3d(${-index * 100}%, 0, 0)` }}
            >
                {reviews.map((r, index) => {
                    console.log(r);
                    return <div className="slide" key={index}>{r[1]}</div>;
                })}
            </div>

            <div className="slideshowDots">
                {reviews.map((_, idx) => (
                    <div
                        key={idx}
                        className={`slideshowDot${
                            index === idx ? " active" : ""
                        }`}
                        onClick={() => {
                            setIndex(idx);
                        }}
                    ></div>
                ))}
            </div>
        </div>
    );
}
