import { useState, useEffect } from "react";
import dayjs from "dayjs";
import range from "lodash-es/range";
import "./calendar.css";
import Navbar from "../../components/navbar/Navbar";
import axios from "axios";

const weekDays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
const timesOfDay = [
    "08:00",
    "08:30",
    "09:00",
    "09:30",
    "10:00",
    "10:30",
    "11:00",
    "11:30",
    "12:00",
    "12:30",
    "13:00",
    "13:30",
    "14:00",
    "14:30",
    "15:00",
    "15:30",
    "16:00",
    "16:30",
    "17:00",
    "17:30",
    "18:00",
    "18:30",
    "19:00",
    "19:30",
    "20:00",
    "20:30",
    "21:00",
    "21:30",
];
const todayObj = dayjs();

export default function Calendar({ viewuser }) {
    const [dayObj, setDayObj] = useState(dayjs());
    const [appsDoctor, setAppsDoctor] = useState([]);

    const [hasSelected, setHasSelected] = useState(false);

    const thisYear = dayObj.year();
    const thisMonth = dayObj.month();
    const daysInMonth = dayObj.daysInMonth();

    const dayObjOf1 = dayjs(`${thisYear}-${thisMonth + 1}-1`);
    const weekDayOf1 = dayObjOf1.day();

    const dayObjOfLast = dayjs(`${thisYear}-${thisMonth + 1}-${daysInMonth}`);
    const weekDayOfLast = dayObjOfLast.day();

    const handlePrev = () => {
        setDayObj(dayObj.subtract(1, "month"));
    };

    const handleNext = () => {
        setDayObj(dayObj.add(1, "month"));
    };

    useEffect(() => {
        const fetchAppsDoctor = async () => {
            const appsForDoctor = await axios.get(`/appointments/:${viewuser._id}`);
            setAppsDoctor(appsForDoctor.data);
        }
    });

    const handleDaySelect = (e) => {
        e.preventDefault();
        const s = document.querySelectorAll(".selected");
        for (const d of s) d.classList.remove("selected");
        e.target.classList.add("selected");
        setDayObj(dayObj.set("date", e.target.textContent));
    };

    const handleTimeSelect = (e) => {
        e.preventDefault();
        const s = document.querySelectorAll(".time-selected");
        for (const d of s) d.classList.remove("time-selected");
        e.target.classList.add("time-selected");
        setDayObj(dayObj.set("hour", e.target.textContent.substring(0,2)).set('minute',e.target.textContent.substring(3)));
    };

    return (
        <>
            <Navbar />
            <div className="calendarWrapper">
                <div className="header">
                    <button
                        type="button"
                        className="nav nav--prev"
                        onClick={handlePrev}
                    >
                        Back
                    </button>
                    <div className="datetime">
                        {dayObj.format("MMM DD YYYY HH:mm")}
                    </div>
                    <button
                        type="button"
                        className="nav nav--prev"
                        onClick={handleNext}
                    >
                        Next
                    </button>
                </div>
                <div className="calendar">
                    <div className="week-container">
                        {weekDays.map((d) => (
                            <div className="week-cell" key={d}>
                                {d}
                            </div>
                        ))}
                    </div>
                    <div className="day-container">
                        {range(weekDayOf1).map((i) => (
                            <div className="day-cell day-cell--faded" key={i}>
                                {dayObjOf1
                                    .subtract(weekDayOf1 - i, "day")
                                    .date()}
                            </div>
                        ))}

                        {range(daysInMonth).map((i) => (
                            <div
                                className={`day-cell day-cell--in-month${
                                    i + 1 === todayObj.date() &&
                                    thisMonth === todayObj.month() &&
                                    thisYear === todayObj.year()
                                        ? " day-cell--today"
                                        : ""
                                }`}
                                key={i}
                                onClick={handleDaySelect}
                            >
                                {i + 1}
                            </div>
                        ))}

                        {range(6 - weekDayOfLast).map((i) => (
                            <div className="day-cell day-cell--faded" key={i}>
                                {dayObjOfLast.add(i + 1, "day").date()}
                            </div>
                        ))}
                    </div>
                </div>
                <div className="timePicker">
                    {timesOfDay.map((t) => (
                        <div className="time-cell" key={t} onClick={handleTimeSelect}>
                            {t} 
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}
