import UserCard from "../../components/userCard/UserCard";
import { useState, useEffect, useContext, useRef } from "react";
import { useParams } from "react-router";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";

export default function Profile() {
    const ProfileDoctor = () => {
        const PF = process.env.REACT_APP_PUBLIC_FOLDER;
        const username = useParams().username;
        const [viewUser, setUser] = useState({});
        const {user} = useContext(AuthContext);

        const detectUser = (fetchCustomer, fetchDoctor) => {
            if(!fetchCustomer && !fetchDoctor) {
                console.log("Invalid username");
                return null;
            }
            else if(fetchCustomer) return fetchCustomer;
            else return fetchDoctor;
        }

        useEffect(()=>{
            const fetchUser = async()=>{
                const resCustomer = await axios.get(`/customers?username=${username}`);
                const resDoctor = await axios.get(`/doctors?username=${username}`);
                setUser(detectUser(resCustomer.data, resDoctor.data));
            }
            fetchUser();
        }, [username]);

        return (
            <>
                <div className="profileTop">
                    <div className="topLeft">
                        <img
                            src={
                                user?.profilePicture
                                    ? PF + user.profilePicture
                                    : PF + "person/noAvatar.png"
                            }
                            alt=""
                            className="profileAvatar"
                        />
                        <div className="profileName">{user.username}</div>
                        <button className="profileBook">Book now</button>
                    </div>
                    <div className="topRight">
                        <div className="infoTop">
                            <div className="infoCard dept">
                                <span className="cardTitle">
                                    Currently Department
                                </span>
                            </div>
                        </div>
                        <div className="infoBottom"></div>
                    </div>
                </div>
                <div className="profileBottom"></div>
            </>
        );
    };

    return (
        <>
            <ProfileDoctor />
        </>
    );
}
